<!doctype html><html><head><?php################################################## read me ######################################## Form-Tag im Kontaktformular:# <form action="107mymailer.php" method="post">## Zur Sicherheit: # Name vom <input type="submit" name="???"> in Variable $SubmitName �bernehmen!## Eingabefeld f�r E-Mail-Adresse sollte den name=eMail haben # <input type="text" name="eMail" maxlength="40"> # (wird dann als Absender verwendet!) #################################################################################################### Eigene Dinge einstellen #############################################################################$SendenAn  ="lauralopezdoval@hotmail.com";			# Ziel-Mail-Adresse$SubmitName="verschicken";				# name="????" im submit-button des Kontaktformulares$Betreff   ="Vom Kontaktformular";		# Betreff der verschickten Mail$TopLine   ="Mailer-Service";			# Text f�r die Topline der Mail#########################################################################################################################################################?><meta charset="utf-8"><title>Mailscript s-zigan.de</title><style>	<!--	body, html, table{ font-family:Verdana, Arial, Helvetica, sans-serif; font-size:11px; background:#eee;}	p 		{ margin:0px; padding:0px;}	h1		{ margin:3px; font-size:16px;}  	hr 		{ width:600px;}	.error 	{color:#CC3300;}	.center { text-align:center; margin-top:50px;}	--></style></head><body><?php	# Auswertung f�r unerw�nschte Zeichen vorbereiten ------------------	$no='/{\";:=}'; 	$all="";	foreach($_POST as $key => $wert){		$all .= $wert;	}	function enti($i){		$i=str_replace("'"," ",$i);		return htmlentities($i, ENT_QUOTES, "UTF-8");	}# m�gliche Fehler abfangen ----------------------------------------------------if(!isset($_POST[$SubmitName])) $txt[]="Script wurde nicht vom Kontaktformular aufgerufen!";if (strcspn($all,$no)!=strlen($all)) $txt[]="unerlaubtes Zeichen: <b>". substr($all,strcspn($all,$no),1) . "</b>";if ($txt == ""){	# HTML-Mail zusammenstellen ---------------------------------------------	$Ausg="	<style> 		* {background:#eee; color:#333; font:12pt Verdana;}		td {padding:0 10px;vertical-align:top;}		.head {background:#333;color:#f60;text-align:center;height:50px;}	</style>	<table>		<tr><th class='head' colspan='2'>&#9776; $TopLine &#9776;</th></tr>";	foreach($_POST as $key => $wert){		if ($key != "sendadr"){			$Ausg .= "<tr><td>$key: </td><td> ";			if($wert == "") $Ausg .=  "-";			else $Ausg .=  enti($wert);			$Ausg .= "</td></tr>";		}	}	$Ausg .= "<tr><th class='head' colspan='2'>&#9827; </th></tr></table>";	 		# Header f�r HTML-Mail zusammenstellen ------------------------------------	if (isset($_POST['eMail']) && $_POST['eMail'])		 $mail_header = "From: $_POST[eMail]\r\n";	else $mail_header = "From: siehe(at)eMail.feld\r\n";	$mail_header .= "MIME-Version: 1.0\r\n";	$mail_header .= "Content-type: text/html; charset=UTF-8 \r\n";		 	# Mail senden -------------------------------------------------------------	# -------------------------------------------------------------------------	mail($SendenAn, $Betreff, "$Ausg", $mail_header);	# -------------------------------------------------------------------------		# Best�tigungs-Bildschirm -------------------------------------------------	echo  "	<div class='center'>			<hr>			<h1>Das Formular wurde gesendet</h1>			<br><br>			Herzlichen Dank f&uuml;r Ihr Interesse.<hr></div>			<script>			<!--				window.setTimeout(\"history.go(-2)\",6000);			// -->			</script>";}# Soweit aufgetreten -> Fehlerausgabe ----------------------------------------if (count($txt)>0){	echo "<div class='center'><hr>";	for ($x=0;$x<count($txt);$x++) echo "<p class='error'>$txt[$x]</p>";	echo "	<hr>Sie werden automatisch zur&uuml;ckgeschalten.</div>			<script>			<!--				window.setTimeout(\"history.go(-1)\",6000);			// -->			</script>";}?></body></html>